mapper.select:
SELECT CALE_CD, USER_CD, CALE_DATE_TIME_START, CALE_DATE_TIME_END, CALE_CN
FROM CALENDAR
WHERE USER_CD = #{id}

mapper.update:
UPDATE CALENDAR
SET
CALE_DATE_TIME_START = #{caleDateTimeStart},
CALE_DATE_TIME_END =
#{caleDateTimeEnd}
WHERE CALE_DATE_TIME_START = #{caleDateOldTimeStart}
